INSERT INTO `products` (`id`, `name`, `image`, `description`, `price`, `category_id`) VALUES
(1, 'Вогнегасник порошковий ВП-2', 'OP-2.jpeg', 'Вогнегасник порошковий з масою заряду 2-кг, призначений для гасіння гасіння класів: А, В,С, а також для гасіння загорянь електрообладнання.', 400, 1),
(2, 'Вогнегасник порошковий ВП-9', 'OP-9.webp', 'Місткість корпусу: 11 ± 0,4 л\r\nМаса вогнегасної речовини: 9 ± 0,8 кг\r\nВогнегасна здатність за класом В-144В; класу А-4А\r\nПризначений термін служби: 10 років', 900, 1),
(3, 'Вогнегасник порошковий ВП-50', 'OP-50.webp', 'Вогнетушитель порошковий ВП-50 – перехідний засіб постачання пожеж, що дозволяє в кризовій ситуації швидко впоратися з невеликими очагами відгорання.', 7200, 1),
(4, 'Кран-комплект HW-25-52 WKV вбудований', 'hw-25.webp', 'Кран комплект HW-25-52WKV – це основний засіб ручного пожежогасіння, що встановлюється у громадських та адміністративних приміщеннях.', 14500, 2),
(5, 'Кран-комплект HW-25-52WKV-2', 'hw-25-52wkv2.webp', 'Пожежний кран-комплект HW-25-52WKV-2 є обов\'язковою частиною пожежного обладнання та входить до системи пожежогасіння у громадських будівлях та спорудах.', 18787, 2),
(6, 'Кран пожежний латунний ДУ-50', '1742053287_ventil-pozharniy.webp', 'Клапан Ду50 – частина пожежного крана, призначений для здійснення подачі води з водопровідної системи внутрішнього або зовнішнього водопроводу за допомогою пожежних рукавів,', 900, 2),
(7, 'Бочка пожежна для води 200л', '1742053659_shchity-i-stendy.jpeg', 'Пожежні бочки металеві, об\'ємом понад 200 літрів використовуються для зберігання води (технічної) на випадок виникнення пожежі, гасіння якої буде здійснюватися за допомогою мотопомп та інших насосних', 2640, 3),
(8, 'Багор пожежний', '1742053933_bagor.jpeg', 'Для гасіння пожежі і подальшого усунення його наслідків використовується велика кількість всіляких інструментів. Вони зберігаються на спеціальних відкритих щитах або закритих стендах, і завжди готові', 180, 3),
(9, 'Сокира пожежна з діелектричною ручкою', '1742054076_topor.jpg', 'Відкритий неконтрольований вогонь досить страшна стихія, тому для ефективної боротьби з нею вкрай важливо застосовувати найефективніші методи боротьби.', 220, 3);

